package com.dzz.medical.config.security;import java.io.Serializable;import java.util.Collection;import lombok.Data;import lombok.EqualsAndHashCode;import lombok.extern.slf4j.Slf4j;import org.springframework.security.core.GrantedAuthority;import org.springframework.security.core.userdetails.User;/** * 自定义用户数据 * @author dzz * @since  2017年06月26 上午9:37 * @version  1.0.0 */@EqualsAndHashCode(callSuper = true)@Data@Slf4jpublic class CustomizeUser extends User implements Serializable{    private static final long serialVersionUID= -6774935433093842478L;    /**用户状态*/    private Integer status;    public CustomizeUser(String username, String password,            Collection<? extends GrantedAuthority> authorities) {        super(username, password, authorities);    }    public CustomizeUser(String username, String password,            Collection<? extends GrantedAuthority> authorities,            Integer status) {        super(username, password, authorities);        this.status = status;    }    public CustomizeUser(String username, String password, boolean enabled, boolean accountNonExpired,            boolean credentialsNonExpired, boolean accountNonLocked,            Collection<? extends GrantedAuthority> authorities,            Integer status) {        super(username, password, enabled, accountNonExpired, credentialsNonExpired, accountNonLocked, authorities);        this.status = status;    }}